import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { Title } from '@angular/platform-browser';

@Component({
  selector: 'fs-login',
  standalone: true,
  imports: [CommonModule, RouterModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  constructor(private readonly titleService: Title) {}

  ngOnInit(): void {
    this.titleService.setTitle('Login | Angular Restaurants');
  }
}
<div class="container">
  <form action="#" id="form-login" class="mt-4" method="POST" role="form">
    <legend>Welcome to FoodScore!</legend>

    <div class="mb-3">
      <label for="correo">Email:</label>
      <input type="email" class="form-control" name="email" id="email" placeholder="Email" required />
    </div>
    <div class="mb-3">
      <label for="password">Password:</label>
      <input type="password" class="form-control" name="password" id="password" placeholder="Password" required />
    </div>
    <p class="text-danger" id="errorInfo"></p>
    <a class="btn btn-secondary" href="/register.html" role="button">Create account</a>
    <button type="submit" class="btn btn-primary" [routerLink]="['/restaurant']">Login</button>
  </form>
</div>
